import React, { useState } from 'react';
import Header from './Components/Header/Header';
import Services from './Components/Services/Services';
import Gallery from './Components/Gallery/Gallery';
import Testimonials from './Components/Testimonials/Testimonials';
import ContactForm from './Components/ContactForm/ContactForm';
import Footer from './Components/Footer/Footer';
import Modal from './Components/Modal/Modal';

const App: React.FC = () => {
  const [activeModal, setActiveModal] = useState<string | null>(null);

  const openModal = (modalType: string) => {
    setActiveModal(modalType);
  };

  const closeModal = () => {
    setActiveModal(null);
  };

  return (
    <>
      <Header onOpenModal={openModal} />

      <Modal isOpen={activeModal === 'services'} onClose={closeModal}>
        <Services />
      </Modal>

      <Modal isOpen={activeModal === 'gallery'} onClose={closeModal}>
        <Gallery />
      </Modal>

      <Modal isOpen={activeModal === 'testimonials'} onClose={closeModal}>
        <Testimonials />
      </Modal>

      <Modal isOpen={activeModal === 'contact'} onClose={closeModal}>
        <ContactForm />
      </Modal>

      <Footer />
    </>
  );
};

export default App;
